#include <stdio.h>
#include <bsoncompare.h>
#include "mongoc-matcher-op-yara.h"

int compare_json(const char *json,
                 const char *jsonspec ){
    bson_error_t error;
    bson_error_t error2;
    bson_t      *spec;
    bson_t      *doc;
    doc = bson_new_from_json (json, -1, &error);
    spec = bson_new_from_json (jsonspec, -1, &error2);
    const uint8_t *spec_bson = bson_get_data(spec);
    const uint8_t *doc_bson = bson_get_data(doc);
    int yes = compare(spec_bson, spec->len, doc_bson, doc->len);
    doc_destroy(spec);
    doc_destroy(doc);
    return yes;
}


int
main (int   argc,
      char *argv[])
{
    bsonsearch_startup();

    BSON_ASSERT(compare_json("{\"k\": [\"not a thing\", \"a thing that it's hello world ok to alert on\", \"shouldnt even compare this\"]}",
                             "{\"k\": {\"$yara\": {\"source\": \"\\nrule example\\n{\\n    strings:\\n        $c = {6c 6f 20 77 6f 72}\\n    condition:\\n        any of them\\n}\\n\"}}}"));

    BSON_ASSERT(!compare_json("{\"k\": [\"not a thing\", \"shouldnt even compare this\"]}",
                             "{\"k\": {\"$yara\": {\"source\": \"\\nrule example\\n{\\n    strings:\\n        $c = {6c 6f 20 77 6f 72}\\n    condition:\\n        any of them\\n}\\n\"}}}"));


    BSON_ASSERT(compare_json("{\"msg\": \"hello world\"}",
                             "{\"msg\": {\"$yara\": {\"$binary\": \"\", \"$type\": \"00\"}}}"));

    BSON_ASSERT(compare_json("{\"msg\": \"hello world\"}",
                             "{\"msg\": {\"$yara\": {\"source\": {\"$binary\": \"\", \"$type\": \"00\"}}}}"));


    // ---------------------------- strings: $c = "ok"
    BSON_ASSERT(compare_json("{\"k\": [\"not a thing\", \"a thing that it's ok to alert on\", \"shouldnt even compare this\"]}",
                             "{\"k\": {\"$yara\": {\"source\":{\"$binary\": \"\", \"$type\": \"00\"}}}}"));


// ---------------------------- strings: $c = "ok"
    BSON_ASSERT(compare_json("{\"k\": [\"not a thing\", \"a thing that it's ok to alert on\", \"shouldnt even compare this\"]}",
                             "{\"k\": {\"$yara\": {\"$binary\": \"\", \"$type\": \"00\"}}}"));


    // ----------------------------{ "k" : b"ok" }
    // ---------------------------- strings: $c = "ok"
    BSON_ASSERT(compare_json("{\"k\": {\"$binary\": \"b2s=\", \"$type\": \"00\"}}",
                              "{\"k\": {\"$yara\": {\"$binary\": \"\", \"$type\": \"00\"}}}"));


    // ----------------------------{ "k" : b"not this" }
    // ---------------------------- strings: $c = "ok"
    BSON_ASSERT(!compare_json("{\"k\": {\"$binary\": \"bm90IHRoaXM=\", \"$type\": \"00\"}}",
                              "{\"k\": {\"$yara\": {\"$binary\": \"\", \"$type\": \"00\"}}}"));


    bsonsearch_shutdown();
    return 0;
}